# Utils / Helpers for LMWT.class MediaInfo(object):	def __init__(self, id = None, title = None, year = None, background = None, poster = None, summary = None, rating = None, dt = None):			self.id = id		self.title = title		self.year = year		self.background = background		self.poster = poster		self.summary = summary		self.rating = rating		self.dt = dt			def __str__(self):			return (			"{ " +			"id: " + str(self.id) + ", " +			"title: " + str(self.title) + ", " +			"year: " + str(self.year) + ", " +			"background:" + str(self.background) + ", " +			"poster: " + str(self.poster) + ", " +			"summary: " + str(self.summary) + ", " +			"rating:" + str(self.rating) + ", " +			"}"		)