# Utils / Helpers for LMWT.class MediaInfo(object):	def __init__(		self, id = None, title = None, type = None, year = None, background = None,		poster = None,  summary = None, rating = None, duration = None, releasedate = None,		genres = [], dt = None	):			self.id = id		self.title = title		self.type = type		self.year = year		self.background = background		self.poster = poster		self.summary = summary		self.rating = rating		self.duration = duration		self.releasedate = releasedate		self.genres = genres		self.dt = dt			def __str__(self):			return (			"{ " +			"id: " + str(self.id) + ", " +			"title: " + str(self.title) + ", " +			"type: " + str(self.type) + ", " +			"year: " + str(self.year) + ", " +			"background:" + str(self.background) + ", " +			"poster: " + str(self.poster) + ", " +			"summary: " + str(self.summary) + ", " +			"rating:" + str(self.rating) + ", " +			"duration:" + str(self.duration) + ", " +			"genres:" + str(self.genres) + ", " +			"release date:" + str(self.releasedate) + ", " +			"}"		)